mapper.update:
UPDATE sharebbs_l 
SET shviewcnt = shviewcnt + 1 
WHERE shareno = #{shareno}

mapper.select:
    SELECT shareno, shtitle, shcontent, filename, shcategory, shviewcnt, shdate,id
		FROM sharebbs_l
		WHERE shareno = #{shareno}

mapper.update:
UPDATE sharebbs_l
set shcontent = #{shcontent},	shtitle = #{shtitle},
filename = #{filename}, shcategory=#{shcategory}
where shareno = #{shareno}
