mapper.update:
	UPDATE memo_l 
	SET mtitle =#{mtitle}, mcontent =#{mcontent}
    WHERE memono = #{memono}

	UPDATE memo_l 
	SET mviewcnt = mviewcnt +1
    WHERE memono = #{memono}

mapper.select:
SELECT memono, mtitle, mcontent, mnick, to_char(mdate, 'yyyy-mm-dd') mdate, mviewcnt, r
FROM(SELECT memono, mtitle, mcontent, mnick, mdate, mviewcnt, rownum r
	FROM (SELECT memono, mtitle, mcontent, mnick, mdate, mviewcnt
		  FROM memo_l
		  
		  
		  title LIKE '%'||#{word}||'%'
		  
		  
		  content LIKE '%'||#{word}||'%'
		  
		  
		  
		  
		  ORDER BY mdate DESC
	)
)

WHERE r >= #{sno} and r <= #{eno}
